public class PointClass {
	float x;
	float y;

	public PointClass() {
		super();
		// TODO Auto-generated constructor stub
	}

	public PointClass(float x, float y) {
		super();
		this.x = x;
		this.y = y;
	}

	public float getX() {
		return x;
	}

	public void setX(float x) {
		this.x = x;
	}

	public float getY() {
		return y;
	}

	public void setY(float y) {
		this.y = y;
	}

}
