public class Task3_Point {
	float x;
	float y;

	public Task3_Point() {
		super();
		// TODO Auto-generated constructor stub
	}

	public Task3_Point(float x, float y) {
		super();
		this.x = x;
		this.y = y;
	}

	public float getX() {
		return x;
	}

	public void setX(float x) {
		this.x = x;
	}

	public float getY() {
		return y;
	}

	public void setY(float y) {
		this.y = y;
	}

}
